import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router";
import NavBar from "../Navbar/Navbar";
import { addReservation } from "../../redux/Actions";
// import DatePicker from 'react-datepicker';
import DatePicker from "react-datepicker";

import "react-datepicker/dist/react-datepicker.css";

export default function Reservas({ match }) {
  const history = useHistory()
  const [startDate, setStartDate] = useState(new Date());

  // function onChange(date){
  //   setDate(date);
  // }
  // const dispatch = useDispatch();


  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setTimeout(() => setLoading(true), 1000);
  }, []);
  // if (!loading) {
  //   return <Loading />;
  // } else {
    return (
      <div>
        <NavBar/>
        <h1>{match.params.nombre}</h1>
        <h3>Seleccione la fecha para su reserva</h3>
        <DatePicker selected={startDate} onChange={(date) => setStartDate(date)} />
        <button className='Button' onClick={()=>{dispatch(addReservation(startDate, match.params.id))}}>Crear reserva</button>       
        <button className='Button' onClick={()=>history.push('/')}>Back to Home</button>       
      </div>
    );

  }
// }
